def minion_game_fast(string):
    vowels = 'AIUEO'
    score_stuart = 0  
    score_kevin = 0  
        
    for el in range(len(string)):
        # loop dilakukan sebesar range string, misalkan 5000
        
        # probability untuk penghitungan score non-vowels (consonant) lebih tinggi dibandingkan vowels
        # maka kondisi ini disimpan didepan
        # string[el:], dimana el = [0,1,2,3,..5000], jika len_string=5000
        # string[0:] = BANANA
        # string[1:] = ANANA
        # string[2:] = NANA

    	if(string[el] not in vowels):
            # skor+1 jika huruf awal bukan huruf vokal

            # skor didasarkan len(string[idx:])

            # kenapa menggunakan panjang string dengan start elemen sekarang?
            # pada case B untuk BANANA, len_str = 6
            # kenapa 6? karena dari B untuk BANANA bisa dihasilkan 6 kemungkinan
            # BANANA
            # BANAN
            # BANA
            # BAN
            # BA
            # B
            score_stuart = score_stuart+len(string[el:])
            # print "stuart"
            # print len(string[el:])
    	else:
            score_kevin = score_kevin+len(string[el:])
            # print "kevin"
            # print len(string[el:])

    if (score_stuart > score_kevin):
        print("Stuart "+str(score_stuart))
    elif(score_stuart < score_kevin):
        print("Kevin "+str(score_kevin))
    else:
    	print("Draw")
    
    # tapi memang masih ada TC yang tidak lolos
    # penggunaan algoritma untuk substring justru makin memperlambat proses penghitungan
    # idenya sebelumnya sudah salah sejak awal
    # kita harus bisa memperhatikan pattern nya






string_1 = "BANANA"
# string_2 = ""
# len string_2 = 5000
# 3039 dan sudah di-kill

# print(len(string_2))
minion_game_fast(string_1)    
